    private void salvarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_salvarActionPerformed
        String dados=nome.getText()+", "+ec.getText()
                + ", "+idade.getText()+" anos.<br/>"
                + endereco.getText()+".<br/>"
                + cidadeuf.getText()+".<br/>"
                + "Telefone: "+fone.getText()+"/Email: "+email.getText();
        try {            
            Gerador.inicio();
            Gerador.corpo(dados);
        } catch (Exception e) {
            e.printStackTrace();
        }
        DadosFormacao formacao = new DadosFormacao();
        formacao.setVisible(true);
        setVisible(false);        
    }//GEN-LAST:event_salvarActionPerformed

